import React, { useEffect, useState } from 'react'

function test() {
    const [paidFor, setPaidFor] = useState(false);
    const [loaded, setLoaded] = useState(false);

    let paypalRef = {
        price: 777.77,
        descriprion: 'testing...',
        img: '/images/logo.jpg'
    };

useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://www.paypal.com/sdk/js?client-id=AaD8da4Hl-J_wkpbDwFo64zfdfcV_i-jOw-fjw33UxhiaL3KddA6IRuPo842lXDV1gQhh2d4PR8ys18F"
    script.addEventListener("laod", () => setLoaded(true));

    document.body.appendChild(script);

    if(loaded) {
        setTimeout(() => {
            window.paypal
                .Buttons({
                    createOrder: (data, action) => {
                        return action.order.create({
                            purchase_units:[
                                {
                                    description: 'test description',
                                    amount: {
                                        currency_code: "EUR",
                                        value: 777.66
                                    }
                                }
                            ]
                        });
                    },
                    onApprove: async (data, actions) => {
                        const order = await actions.order.capture();
                        setPaidFor(true);
                        console.log(order);
                    },
                })
                .render(paypalRef);
        });
    }
});

    return (
        <div className="test">
            {paidFor ? (
                <div>
                    <h1>congrats. you paid</h1>
                    <img className="logo" alt="" src="/images/logo.jpg" />
                </div>
            ) : (
                <div>
                    <h1>wanna pay?</h1>
                    <img className="logo" alt="" src="/images/logo.jpg" />
                    <div ref={v => (paypalRef = v)} />
                </div>
            )}
        </div>
    )
}

export default test
