import React from 'react'
import PayWithPayPal from './Payment/PayWithPayPal'
import styled from 'styled-components';

const StyledDonations = styled.div`
    padding-top: 30px;
    background-color: #f1f1e8;

    @media (min-width: 769px) {
      display: flex;
      columns: 50% 2;
      flex-wrap: wrap;
    }

    .donation__card {
        width: 330px;
        margin:10px auto;
        background-color: white;
        border: 1px solid grey;
        border-radius: 10px;
        padding: 10px;
        box-shadow: 2px 5px #888888;
    }
    
`

function Donations() {
    return (
        <StyledDonations>
            <div className="donation__card">
                <PayWithPayPal item={{name:'test', value:100.10}} />
            </div>
            <div className="donation__card">
                <PayWithPayPal item={{name:'test2', value:99.10}} />
            </div>
            <div className="donation__card">
                <PayWithPayPal item={{name:'test2', value:99.10}} />
            </div>
        </StyledDonations>
    )
}

export default Donations
